('tafsiri_ot','',5,6 ,'و ﺑﺮای ﻗﺮﺑﺎﻧﯽ ﮔﻨﺎه ﺧﻮد ﻳﮏ ﺑﺮه ﻳﺎ ﺑﺰ ﻣﺎده ﻧﺰد ﺧﺪاوﻧﺪ ﺑﻴﺎورد ﺗﺎ آﺎهﻦ ﺑﺮاﻳﺶ آﻔﺎره آﻨﺪ. 7اﮔﺮ ﺷﺨﺺ ﻣﺠﺮم ﺗﻨﮕﺪﺳﺖ ﺑﺎﺷﺪ و ﻧﺘﻮاﻧﺪ ﺑﺮهای ﺑﻴﺎورد، ﻣﯽﺗﻮاﻧﺪ دو ﻗﻤﺮی ﻳﺎ دو ﺟﻮﺟﻪ آﺒﻮﺗﺮ ﺑﺮای آﻔﺎره ء ﮔﻨﺎه ﺧﻮد ﺑﻪ ﺧﺪاوﻧﺪ ﺗﻘﺪﻳﻢ ،آﻨﺪ ﻳﻜﯽ ﺑﺮای ﻗﺮﺑﺎﻧﯽ ﮔﻨﺎه و دﻳﮕﺮی ﺑﺮای ﻗﺮﺑﺎﻧﯽ ﺳﻮﺧﺘﻨﯽ.',NULL),
('tafsiri_ot','',15,5 ,'آﺗﺶ ﻗﺮﺑﺎﻧﮕﺎه ﺧﺎﻣﻮش ﻧﺸﻮد.',NULL),
('tafsiri_ot','',19,20 ,'ﺧﺪاوﻧﺪ هﻤﭽﻨﻴﻦ اﻳﻦ دﺳﺘﻮرات را ﺑﻪ ﻣﻮﺳﯽ داد: هﺎرون و هﺮ آﺪام از ﭘﺴﺮاﻧﺶ در روزی آﻪ ﺑﺮای ﻣﻘﺎم آﺎهﻨﯽ اﻧﺘﺼﺎب ﻣﯽﺷﻮﻧﺪ ﺑﺎﻳﺪ ﻳﮏ آﻴﻠﻮ آرد ﻣﺮﻏﻮب، ﺑﻪ اﻧﺪازه ء هﺪﻳﻪ ء آردی روزاﻧﻪ ﺑﻪ ﺣﻀﻮر ﺧﺪاوﻧﺪ ﺑﻴﺎورﻧﺪ وﻧﺼﻒ آن را در ﺻﺒﺢ و ﻧﺼﻒ دﻳﮕﺮ را در ﻋﺼﺮ ﺑﻪ ﺧﺪاوﻧﺪ ﺗﻘﺪﻳﻢ آﻨﻨﺪ. 21اﻳﻦ هﺪﻳﻪ ﺑﺎﻳﺪ روی ﺳﺎج ﺑﺎ روﻏﻦ زﻳﺘﻮن ﭘﺨﺘﻪ ﺷﻮد ﺳﭙﺲ ﺗﻜﻪﺗﻜﻪ ،ﺷﺪه ﺑﻌﻨﻮان هﺪﻳﻪ ء آردی ﺑﻪ ﺧﺪاوﻧﺪ ﺗﻘﺪﻳﻢ ﺗﻘﺪﻳﻢ آﻨﺪ. ﻗﻮچ ﺑﺎﻳﺪ ﻣﻄﺎﺑﻖ ﻣﻌﻴﺎر رﺳﻤﯽ، ارزﻳﺎﺑﯽ ﺷﻮد. 7آﺎهﻦ ﺑﺎ اﻳﻦ ﻗﺮﺑﺎﻧﯽ ﺑﺮای او در ﺣﻀﻮر ﺧﺪاوﻧﺪ آﻔﺎره ﺧﻮاهﺪ آﺮد و ﮔﻨﺎه او ﺑﺨﺸﻴﺪه ﺧﻮاهﺪ ﺷﺪ. ﻣﻘﺮرات ﻗﺮﺑﺎﻧﯽ ﺳﻮﺧﺘﻨﯽ 8و9آﻧﮕﺎه ﺧﺪاوﻧﺪ ﺑﻪ ﻣﻮﺳﯽ ﻓﺮﻣﻮد آﻪ در ﻣﻮرد ﻗﺮﺑﺎﻧﯽ ﺳﻮﺧﺘﻨﯽ اﻳﻦ ﻣﻘﺮرات را ﺑﻪ هﺎرون و ﭘﺴﺮاﻧﺶ ﺑﺪهﺪ: ﻗﺮﺑﺎﻧﯽ ﺳﻮﺧﺘﻨﯽ ﺑﺎﻳﺪ ﺗﻤﺎم ﺷﺐ ﺑﺮ ﻗﺮﺑﺎﻧﮕﺎه ﺑﺎﺷﺪ و ﻻوﻳﺎن',NULL),
('tafsiri_ot','',19,6 ,'ﺟﺎی ﻣﻘﺪﺳﯽ ﺧﻮرده ﺷﻮد. اﻳﻦ ،ﻗﺮﺑﺎﻧﯽ ﺑﺴﻴﺎر ﻣﻘﺪس ﻣﻮرد ﭘﺴﻨﺪ ﺧﺪاوﻧﺪ ﺧﻮاهﺪ ﺑﻮد. هﺪﻳﻪ اﻳﻦ ﮔﺮدد. اﺳﺖ. 7ﻗﺎﻧﻮﻧﯽ آﻪ ﺑﺎﻳﺪ در ﻣﻮرد ﻗﺮﺑﺎﻧﯽ ﮔﻨﺎه و ﻗﺮﺑﺎﻧﯽ ﺟﺒﺮان رﻋﺎﻳﺖ ﺷﻮد اﻳﻦ اﺳﺖ: ﮔﻮﺷﺖ ﻗﺮﺑﺎﻧﯽ ﺑﻪ آﺎهﻨﯽ ﺗﻌﻠﻖ دارد آﻪ ﻣﺮاﺳﻢ آﻔﺎره را اﺟﺮا ﻣﯽآﻨﺪ. 8) در ﺿﻤﻦ ﭘﻮﺳﺖ ﻗﺮﺑﺎﻧﯽ ﺳﻮﺧﺘﻨﯽ ﻧﻴﺰ ﺑﻪ آن آﺎهﻦ ﺗﻌﻠﻖ دارد(.',NULL),
('tafsiri_ot','',22,23 ,'هﺮ آﺎهﻨﯽ آﻪ ﺑﺠﺎی ﭘﺪر ،ﺧﻮد آﺎهﻦ اﻋﻈﻢ ﻣﯽﺷﻮد ﺑﺎﻳﺪ در روز اﻧﺘﺼﺎب ﺧﻮد هﻤﻴﻦ هﺪﻳﻪ را ﺗﻘﺪﻳﻢ آﻨﺪ. اﻳﻦ ﻓﺮﻳﻀﻪای اﺑﺪی اﺳﺖ. اﻳﻦ هﺪﻳﻪ ﺑﺎﻳﺪ ﺗﻤﺎﻣًﺎ در ﺣﻀﻮر ﺧﺪاوﻧﺪ ﺳﻮزاﻧﺪه ﺷﻮد و ﭼﻴﺰی از آن ﺧﻮرده ﻧﺸﻮد. ﻣﻘﺮرات ﻗﺮﺑﺎﻧﯽ ﮔﻨﺎه 24و25ﺧﺪاوﻧﺪ ﺑﻪ ﻣﻮﺳﯽ ﻓﺮﻣﻮد آﻪ در ﻣﻮرد ﻗﺮﺑﺎﻧﯽ ﮔﻨﺎه اﻳﻦ ﻣﻘﺮرات را ﺑﻪ هﺎرون و ﭘﺴﺮاﻧﺶ ﺑﺪهﺪ: اﻳﻦ ﻗﺮﺑﺎﻧﯽ ﺑﺴﻴﺎر ﻣﻘﺪس اﺳﺖ و ﺑﺎﻳﺪ در ﺣﻀﻮر ﺧﺪاوﻧﺪ در ﺟﺎﻳﯽ ذﺑﺢ ﺷﻮد آﻪ ﻗﺮﺑﺎﻧﯽهﺎی ﺳﻮﺧﺘﻨﯽ را ﺳﺮ ﻣﯽﺑﺮﻧﺪ.',NULL),
('tafsiri_ot','',16,18 ,'وﻟﯽ هﺮ ﭼﻪ ﺗﺎ روز ﺳﻮم ﺑﺎﻗﯽ ﺑﻤﺎﻧﺪ، ﺑﺎﻳﺪ ﺳﻮزاﻧﺪ. اﮔﺮ در روز ﺳﻮم ﭼﻴﺰی از ﮔﻮﺷﺖ ﻗﺮﺑﺎﻧﯽ ﺧﻮرده ،ﺷﻮد ﺧﺪاوﻧﺪ ،ﭘﻴﻪ داﺧﻞ ﺷﻜﻢ',NULL),
('tafsiri_ot','',16,4 ,'و ﻧﻴﺰ ﻗﻠﻮههﺎ و ﭼﺮﺑﯽ روی آﻧﻬﺎ، و ﺳﻔﻴﺪی روی ﺟﮕﺮ ﻣﯽﺷﻮد روی ﻗﺮﺑﺎﻧﮕﺎه ﺑﮕﺬارد.',NULL),
('tafsiri_ot','',6,7 ,'اﻧﺘﺼﺎب هﺎرون و ﭘﺴﺮاﻧﺶ ﺑﻪ ﺧﺪﻣﺖ ،ﺧﺪاوﻧﺪ ﺑﻪ اﻳﺸﺎن داده ﺷﺪ.',NULL),
('tafsiri_ot','',37,38 ,'اﻳﻨﻬﺎ ﻣﻘﺮراﺗﯽ ﺑﻮد درﻣﻮرد ﻗﺮﺑﺎﻧﯽ ،ﺳﻮﺧﺘﻨﯽ هﺪﻳﻪ ء ،آردی ﻗﺮﺑﺎﻧﯽ ،ﮔﻨﺎه ﻗﺮﺑﺎﻧﯽ ،ﺟﺒﺮان ﻗﺮﺑﺎﻧﯽ ﺗﻘﺪﻳﺲ و ﻗﺮﺑﺎﻧﯽ ﺳﻼﻣﺘﯽ آﻪ ﺧﺪاوﻧﺪ در ﺑﻴﺎﺑﺎن، در آﻮه ﺳﻴﻨﺎ ﺑﻪ ﻣﻮﺳﯽ داد ﺗﺎ ﻗﻮم اﺳﺮاﺋﻴﻞ ﺑﺪاﻧﻨﺪ ﭼﮕﻮﻧﻪ ﻗﺮﺑﺎﻧﯽهﺎی ﺧﻮد را ﺑﻪ ﺧﺪاوﻧﺪ ﺗﻘﺪﻳﻢ آﻨﻨﺪ. ﻣﺮاﺳﻢ ﺗﻘﺪﻳﺲ هﺎرون و ﭘﺴﺮاﻧﺶ (37-',NULL),
('tafsiri_ot','',37,29 ,'ﺧﺮوج) ﺧﺪاوﻧﺪ ﺑﻪ ﻣﻮﺳﯽ ﻓﺮﻣﻮد: »هﺎرون و ﭘﺴﺮاﻧﺶ آن را ﺑﺨﻮرد ﻣﺠﺮم ﻣﯽﺑﺎﺷﺪ. 19ﮔﻮﺷﺘﯽ را آﻪ ﺑﻪ ﭼﻴﺰی ﻧﺠﺲ ﺧﻮرده اﺳﺖ ﻧﺒﺎﻳﺪ ،ﺧﻮرد ﺑﻠﻜﻪ ﺑﺎﻳﺪ آن را ﺳﻮزاﻧﺪ. ﮔﻮﺷﺖ ﻗﺮﺑﺎﻧﯽ را ﻓﻘﻂ آﺴﺎﻧﯽ ﻣﯽﺗﻮاﻧﻨﺪ ﺑﺨﻮرﻧﺪ آﻪ ﻃﺎهﺮ هﺴﺘﻨﺪ.',NULL),
('tafsiri_ot','',21,23 ,'ﺧﺪاوﻧﺪ ﺑﻪ ﻣﻮﺳﯽ ﻓﺮﻣﻮد آﻪ اﻳﻦ دﺳﺘﻮرات را ﺑﻪ ﻗﻮم اﺳﺮاﺋﻴﻞ ﺑﺪهﺪ: هﺮﮔﺰ ﭘﻴﻪ ﮔﺎو و ﮔﻮﺳﻔﻨﺪ و ﺑﺰ را ﻧﺨﻮرﻳﺪ. 24ﭘﻴﻪ ﺣﻴﻮاﻧﯽ آﻪ ﻣﺮده ﻳﺎ ﺗﻮﺳﻂ ﺟﺎﻧﻮری',NULL),
('tafsiri_ot','',7,9 ,'و ﭼﻨﺎﻧﻜﻪ ﺧﺪاوﻧﺪ ﻓﺮﻣﻮده ،ﺑﻮد ﻋﻤﺎﻣﻪ را ﺑﺮ ﺳﺮ هﺎرون ﻧﻬﺎد و ﻧﻴﻢ ﺗﺎج ﻣﻘﺪس را آﻪ از ﻃﻼ ﺑﻮد ﺟﻠﻮ ﻋﻤﺎﻣﻪ ﻧﺼﺐ آﺮد.',NULL),
('tafsiri_ot','',25,27 ,'هﺮﮔﺰ ﺧﻮن ﻧﺨﻮرﻳﺪ، ﻧﻪ ﺧﻮن ﭘﺮﻧﺪه و ﻧﻪ ﺧﻮن ﺣﻴﻮان ﭼﻬﺎرﭘﺎ. هﺮآﺲ، در هﺮﺟﺎ، ﺧﻮن ﺑﺨﻮرد، ﺑﺎﻳﺪ از ﻣﻴﺎن ﻗﻮم ﺧﺪا ﻃﺮد ﺷﻮد. ﺳﻬﻢ ﺁﺎهﻦ از ﻗﺮﺑﺎﻧﯽ',NULL),
('tafsiri_ot','',28,30 ,'ﺧﺪاوﻧﺪ ﺑﻪ ﻣﻮﺳﯽ ﻓﺮﻣﻮد آﻪ اﻳﻦ دﺳﺘﻮرات را ﺑﻪ ﻗﻮم اﺳﺮاﺋﻴﻞ ﺑﺪهﺪ: هﺮآﺲ ﺑﺨﻮاهﺪ ﻗﺮﺑﺎﻧﯽ ﺳﻼﻣﺘﯽ ﺑﻪ ﺧﺪاوﻧﺪ ﺗﻘﺪﻳﻢ آﻨﺪ ﺑﺎﻳﺪ ﺑﺎ دﺳﺖ ﺧﻮد آن ﻗﺮﺑﺎﻧﯽ را آﻪ ﺑﺮ آﺗﺶ ﺑﻪ ﺧﺪاوﻧﺪ ﺗﻘﺪﻳﻢ ،ﻣﯽﺷﻮد ﺑﻴﺎورد. ﭘﻴﻪ و ﺳﻴﻨﻪ را ﺟﻠﻮ ﻗﺮﺑﺎﻧﮕﺎه ﺗﻘﺪﻳﻢ آﻨﺪ و ﺳﻴﻨﻪ ء ﻗﺮﺑﺎﻧﯽ را ﺑﻌﻨﻮان هﺪﻳﻪ ء ،ﻣﺨﺼﻮص در ﺣﻀﻮر ﺧﺪاوﻧﺪ ﺗﻜﺎن دهﺪ.',NULL),
('tafsiri_ot','',32,33 ,'ران راﺳﺖ ،ﻗﺮﺑﺎﻧﯽ ﺑﻌﻨﻮان هﺪﻳﻪ ء ،ﻣﺨﺼﻮص ﺑﻪ آﺎهﻨﯽ داده ﺷﻮد آﻪ ﺧﻮن و ﭘﻴﻪ ﻗﺮﺑﺎﻧﯽ را ﺗﻘﺪﻳﻢ ﻣﯽآﻨﺪ؛',NULL),
('tafsiri_ot','',25,26 ,'و از داﺧﻞ ﺳﺒﺪ ﻧﺎن ﺑﺪون ﺧﻤﻴﺮﻣﺎﻳﻪ آﻪ در ﺣﻀﻮر ﺧﺪاوﻧﺪ ،ﺑﻮد ﻳﮏ ﻗﺮص ﻧﺎن ﺑﺪون ،ﺧﻤﻴﺮﻣﺎﻳﻪ ﻳﮏ ﻗﺮص ﻧﺎن روﻏﻨﯽ و ﻳﮏ ﻧﺎن ﻧﺎزک ﺑﺮداﺷﺖ و آﻧﻬﺎ را روی ﭘﻴﻪ و ران راﺳﺖ ﮔﺬاﺷﺖ.',NULL),
('tafsiri_ot','',24,9 ,'ﻗﺮﺑﺎﻧﮕﺎه ﺳﻮزاﻧﻴﺪ. 14دل و روده و ﭘﺎﭼﻪهﺎ را ﺷﺴﺖ و اﻳﻨﻬﺎ را ﻧﻴﺰ ﺑﻌﻨﻮان ﻗﺮﺑﺎﻧﯽ ﺳﻮﺧﺘﻨﯽ ﺑﺮ ﻗﺮﺑﺎﻧﮕﺎه ﺳﻮزاﻧﻴﺪ. ﺗﻘﺪﻳﻢ ﻗﺮﺑﺎﻧﯽ ﺗﻮﺳﻂ ﺁﺎهﻨﺎن در روز ،هﺸﺘﻢ ،ﻣﻮﺳﯽ هﺎرون و ﭘﺴﺮاﻧﺶ را',NULL),
('tafsiri_ot','',9,2 ,'و ﺑﻪ هﺎرون ﻗﺮﺑﺎﻧﯽ ﮔﻨﺎه ﻗﻮم را ذﺑﺢ ،ﻧﻤﻮده آن را ﻣﺎﻧﻨﺪ ﻗﺮﺑﺎﻧﯽ ﮔﻨﺎه ﺧﻮد ﺑﺮای ﮔﻨﺎه ﻗﻮم ﺗﻘﺪﻳﻢ آﺮد.',NULL),
('tafsiri_ot','',6,24 ,'و از ﺣﻀﻮر ﺧﺪاوﻧﺪ آﺗﺶ ﻓﺮود ،آﻣﺪه ﻗﺮﺑﺎﻧﯽ ﺳﻮﺧﺘﻨﯽ و ﭘﻴﻪ روی ﻗﺮﺑﺎﻧﮕﺎه را ﺑﻠﻌﻴﺪ. را اﻧﺠﺎم دهﻴﺪ ﺗﺎ ﺣﻀﻮر ﭘﺮﺟﻼل ﺧﻮد را ﺑﺮ ﺷﻤﺎ ﻇﺎهﺮ آﻨﺪ«. 7آﻧﮕﺎه ﻣﻮﺳﯽ ﺑﻪ هﺎرون ﮔﻔﺖ: »ﻧﺰدﻳﮏ ﻗﺮﺑﺎﻧﮕﺎه ﺑﻴﺎ و هﻤﺎﻧﻄﻮر آﻪ ﺧﺪاوﻧﺪ ﻓﺮﻣﻮده اﺳﺖ ﻗﺮﺑﺎﻧﯽ ﮔﻨﺎه و ﻗﺮﺑﺎﻧﯽ ﺳﻮﺧﺘﻨﯽ ﺧﻮد را ﺗﻘﺪﻳﻢ آﺮده، ﺑﺮای ﺧﻮد آﻔﺎره ،آﻦ و ﺳﭙﺲ ﻗﺮﺑﺎﻧﯽهﺎی ﻗﻮم را ﺗﻘﺪﻳﻢ ﻧﻤﻮده ﺑﺮای آﻧﻬﺎ آﻔﺎره ﻧﻤﺎ«.',NULL),
