('tafsiri_ot','',23,24 ,'ﺑﻜﻨﯽ؟ آﺎر ﺧﻮب ﻣﯽﺗﻮاﻧﯽ اﻳﻦ » ﻣﺮدم ﺧﺪاوﻧﺪ ﻣﯽﻓﺮﻣﺎﻳﺪ: ﺑﮕﻮ آﻪ ﺑﻪ اﻳﺸﺎن را ﭘﻴﺮوی دروﻏﻴﻦ و ﺧﺪاﻳﺎن آﺮده ﻓﺮاﻣﻮش ﺳﺮزﻣﻴﻦ را ﻣﺎﻧﻨﺪ آﺴﺎﻧﯽ آﻪ ﻣﺴﺖ ،ﺷﺪهاﻧﺪ ﮔﻴﺞ ﺧﻮاهﻢ ﻧﻤﻮدهای، ﻣﻦ هﻢ ﺗﻮ را ﭘﺮاآﻨﺪه ﻣﯽآﻨﻢ، هﻤﺎﻧﻄﻮر آﻪ ﺑﺎد ﺻﺤﺮا آﺎه را ﭘﺮاآﻨﺪه ﻣﯽﺳﺎزد؛ اﻳﻦ اﺳﺖ آن ﺳﺮﻧﻮﺷﺘﯽ آﻪ ﺑﺮاﻳﺖ ﺗﻌﻴﻴﻦ آﺮدهام.',NULL),
('tafsiri_ot','',11,13 ,'» آﺴﯽ ﻧﻤﯽﺗﻮاﻧﺪ ﻣﻴﻠﻪهﺎی آهﻨﯽ را ،ﺑﺸﻜﻨﺪ ﺑﺨﺼﻮص آهﻦ ﺳﺮزﻣﻴﻨﻬﺎی ﺷﻤﺎل* را آﻪ ﺑﺎ ﻣﻔﺮغ ﻣﺨﻠﻮط ﺷﺪه ﺑﺎﺷﺪ؛ هﻤﻴﻨﻄﻮر ﺳﺮﺳﺨﺘﯽ اﻳﻦ ﻗﻮم را ﻧﻴﺰ آﺴﯽ ﻧﻤﯽﺗﻮاﻧﺪ درهﻢ ﺑﺸﻜﻨﺪ! ﭘﺲ ﺑﺴﺒﺐ هﻤﻪ ء ﮔﻨﺎهﺎﻧﺸﺎن در ﺗﻤﺎم اﻳﻦ ،ﺳﺮزﻣﻴﻦ ﺛﺮوت و ﮔﻨﺠﻬﺎﻳﺸﺎن را ﺑﻌﻨﻮان ﻏﻨﻴﻤﺖ ﺑﺪﺳﺖ دﺷﻤﻦ ﺧﻮاهﻢ ﺳﭙﺮد.',NULL),
('tafsiri_ot','',8,19 ,'ﺧﻮاهﺪ ﻧﺸﺴﺖ؛ ﺣﺘﯽ در ﻣﺮگ واﻟﺪﻳﻨﺸﺎن ﻧﻴﺰ هﻴﭽﻜﺲ ﺑﺎ اﻳﺸﺎن هﻤﺪردی ﻧﺨﻮاهﺪ آﺮد!',NULL),
('tafsiri_ot','',14,15 ,'ﺑﺎ اﻳﻨﺤﺎل ﺧﺪاوﻧﺪ ﻣﯽﻓﺮﻣﺎﻳﺪ: »زﻣﺎﻧﯽ ﻣﯽآﻳﺪ آﻪ ﺑﻴﻤﺎرﻳﻬﺎی آﺸﻨﺪه ﺧﻮاهﻨﺪ ﻣﺮد؛ آﺴﯽ ﺑﺮای آﻧﻬﺎ ﻣﺎﺗﻢ ﻧﺨﻮاهﺪ ﮔﺮﻓﺖ؛ ﺟﻨﺎزههﺎﻳﺸﺎن دﻓﻦ ﻧﺨﻮاهﺪ ﺷﺪ ﺑﻠﻜﻪ هﻤﭽﻮن ﻓﻀﻠﻪ ﺑﺮ روی زﻣﻴﻦ ﺑﺎﻗﯽ ﺧﻮاهﺪ ﻣﺎﻧﺪ. آﻧﻬﺎ ﻣﺮدم هﺮﮔﺎه ﺑﺨﻮاهﻨﺪ درﻣﻮرد آﺎرهﺎی ﺷﮕﻔﺖاﻧﮕﻴﺰ ﺧﻮاهﻨﺪ ﺷﺪ و آﺸﺘﻪ و ﻗﺤﻄﯽ در اﺛﺮ ﺟﻨﮓ ﻣﻦ ﮔﻔﺘﮕﻮ آﻨﻨﺪ، دﻳﮕﺮ اﻋﻤﺎل ﻋﺠﻴﺐ ﻣﺮا ﺑﻪ هﻨﮕﺎم ﺑﻴﺮون آوردن ﺑﻨﯽاﺳﺮاﺋﻴﻞ از ،ﻣﺼﺮ ذآﺮ ﻧﺨﻮاهﻨﺪ ﻧﻤﻮد، ﺑﻠﻜﻪ در اﻳﻦ ﺑﺎره ﺳﺨﻦ ﺧﻮاهﻨﺪ ﮔﻔﺖ آﻪ ﻣﻦ ﭼﮕﻮﻧﻪ ﺑﻨﯽاﺳﺮاﺋﻴﻞ را از ﺳﺮزﻣﻴﻦ ﺷﻤﺎل و هﻤﻪ ﻻﺷﻪهﺎﻳﺸﺎن را ﻻﺷﺨﻮرهﺎ و ﺟﺎﻧﻮران ﺧﻮاهﻨﺪ ﺧﻮرد.',NULL),
('tafsiri_ot','',16,7 ,'و ﻧﻪ آﺴﯽ ﺑﺮای ﺗﺴﻠﯽﺷﺎن ﺑﺎ آﻧﻬﺎ ﺑﺮ ﺳﺮ ﺳﻔﺮه ﻣﯽﻓﺮﺳﺘﻢ ﺗﺎ ﺑﻴﺎﻳﻨﺪ و ﺷﻤﺎ را از اﻋﻤﺎق درﻳﺎ آﻪ در آﻧﺠﺎ از ﺗﺮس ﺧﺸﻢ ﻣﻦ ﺧﻮد را ﭘﻨﻬﺎن ،آﺮدهاﻳﺪ ﺻﻴﺪ * ﺗﺮاﺷﻴﺪن ﻣﻮی ﺳﺮ, ﻳﮑﯽ از رﺳﻮم ﻋﺰاداری ﺑﺖﭘﺮﺳﺘﺎن ﺑﻮد. ارﻣﻴﺎ',NULL),
('tafsiri_ot','',16,20 ,'ﻣﺜﻞ ﺑﻮﺗﻪای اﺳﺖ آﻪ در ﺑﻴﺎﺑﺎن ﺧﺸﮏ و ﺳﻮزان و در ﺷﻮرهزارهﺎ ،ﻣﯽروﻳﺪ ﺟﺎﻳﯽ آﻪ هﻴﭻ ﮔﻴﺎه دﻳﮕﺮی وﺟﻮد ﻧﺪارد؛ او هﺮﮔﺰ ﺧﻴﺮ و ﺑﺮآﺖ ﻧﺨﻮاهﺪ دﻳﺪ!',NULL),
('tafsiri_ot','',12,13 ,'ای ،ﺧﺪاوﻧﺪ ﺗﺨﺖ ﺑﻠﻨﺪ و ﺑﺎﺷﻜﻮه و اﺑﺪی ﺗﻮ',NULL),
('tafsiri_ot','',2,3 ,'ﺟﻮاﻧﺎﻧﺘﺎن اﺳﺖ. ﺷﺪه ،ﻧﻤﯽﻣﺎﻧﻨﺪ زﻳﺮ هﺮ درﺧﺖ ﺳﺒﺰ و روی هﺮ آﻮه ﺑﻠﻨﺪ ﻧﻮﺷﺘﻪهﺎی روی ﺧﺎک ﻣﺤﻮ ﺧﻮاهﻨﺪ ،ﺷﺪ ﭼﻮن ﺧﺪاوﻧﺪ آﺮدهاﻧﺪ. ،اﺳﺖ ﺗﺮک ﺣﻴﺎت ﭼﺸﻤﻪ آب را آﻪ ﺑﺖ ﻣﯽﭘﺮﺳﺘﻨﺪ؛ ﭘﺲ ﺑﺴﺒﺐ ،ﮔﻨﺎهﺎﻧﺘﺎن ﺗﻤﺎم ﮔﻨﺠﻬﺎ و',NULL),
('tafsiri_ot','',16,4 ,'و ﻣﺠﺒﻮر ﺧﻮاهﻴﺪ ﺷﺪ اﻳﻦ ﺳﺮزﻣﻴﻦ را آﻪ ﺑﻪ ﻣﻴﺮاث ﺑﻪ ﺷﻤﺎ داده ﺑﻮدم ﺗﺮک آﻨﻴﺪ و دﺷﻤﻨﺎﻧﺘﺎن را در ﺳﺮزﻣﻴﻨﻬﺎی دور دﺳﺖ ﺑﻨﺪﮔﯽ ،ﻧﻤﺎﻳﻴﺪ ﭼﻮن آﺗﺶ ﺧﺸﻢ ﻣﺮا ﺷﻌﻠﻪور ﺳﺎﺧﺘﻪاﻳﺪ، آﺗﺸﯽ آﻪ هﺮﮔﺰ ﺧﺎﻣﻮش ﻧﺨﻮاهﺪ ﺷﺪ! ﮔﻔﺘﺎر ﮔﻮﻧﺎﮔﻮن',NULL),
('tafsiri_ot','',6,21 ,'آﻧﻬﺎ را از ﺑﻴﻦ ﺑﺒﺮد و هﻴﭽﻜﺲ ﻧﺘﻮاﻧﺪ ﺷﻌﻠﻪهﺎی آن را ﺧﺎﻣﻮش آﻨﺪ«. ارﻣﻴﺎ ﻧﺰد ﺁﻮزهﮔﺮ ﺧﺪاوﻧﺪ ﺑﻪ ﻣﻦ ﻓﺮﻣﻮد: ﻧﺒﻮدهام؛ ﺗﻮ ﺧﻮب ﻣﯽداﻧﯽ آﻪ ﻣﻦ ﺗﻨﻬﺎ هﺸﺪارهﺎی ﺗﻮ را ﺑﻪ اﻳﺸﺎن اﻋﻼم آﺮدهام.',NULL);
INSERT IGNORE INTO `bible` (version, book, chapter, verse_number, text_fa, text_en) VALUES
('tafsiri_ot','',8,9 ,'و اﮔﺮ اﻋﻼم آﻨﻢ آﻪ ﻣﯽﺧﻮاهﻢ ﻗﻮﻣﯽ ﻳﺎ ﻣﻤﻠﻜﺘﯽ را ﻗﺪرﺗﻤﻨﺪ و ﺑﺰرگ ،ﺳﺎزم 10اﻣﺎ آن ﻗﻮم راه و روش ﺧﻮد را ﺗﻐﻴﻴﺮ ،داده ﺑﺪﻧﺒﺎل ﺷﺮارت ﺑﺮود و اﺣﻜﺎم ﻣﺮا اﻃﺎﻋﺖ ،ﻧﻜﻨﺪ آﻧﮕﺎه ﻣﻦ ﻧﻴﺰ ﻧﻴﻜﻮﻳﯽ و ﺑﺮآﺘﯽ را آﻪ در ﻧﻈﺮ ،داﺷﺘﻢ ﺑﻪ آن ﻗﻮم ﻧﺨﻮاهﻢ داد.',NULL),
('tafsiri_ot','',24,25 ,'آﻧﮕﺎه ﻗﻮم ﺷﻤﺎ هﻤﻴﺸﻪ ﭘﺎﻳﺪار ﺧﻮاهﺪ ،ﻣﺎﻧﺪ و از دودﻣﺎن ﺳﻠﻄﻨﺘﯽ داود هﻤﻴﺸﻪ ﻳﮏ ﻧﻔﺮ در اورﺷﻠﻴﻢ ﺳﻠﻄﻨﺖ ﺧﻮاهﺪ آﺮد. اﻳﻦ ﺷﻬﺮ ﺑﺮای هﻤﻴﺸﻪ آﺑﺎد ﺧﻮاهﺪ ﻣﺎﻧﺪ و هﻤﻮاره ﭘﺎدﺷﺎهﺎن و ﺣﻜﻤﺮاﻧﺎن ﺳﻮار ﺑﺮ ﻋﺮاﺑﻪهﺎ و اﺳﺒﺎن، ﺑﺎ ﺷﻜﻮه و ﺟﻼل در ﻣﻴﺎن ﻣﺮدم رﻓﺖ و آﻣﺪ ﺧﻮاهﻨﺪ ﻧﻤﻮد. 26از اﻃﺮاف اورﺷﻠﻴﻢ و از ﺷﻬﺮهﺎی ﻳﻬﻮدا و ﺳﺮزﻣﻴﻦ ﺑﻨﻴﺎﻣﻴﻦ و از دﺷﺘﻬﺎ و آﻮهﺴﺘﺎﻧﻬﺎ و ﺟﻨﻮب ﻳﻬﻮدا ﻣﺮدم هﻤﻪ ﺧﻮاهﻨﺪ آﻣﺪ و ﻗﺮﺑﺎﻧﯽهﺎی ﮔﻮﻧﺎﮔﻮن ﺑﻪ ﺧﺎﻧﻪ ﺧﺪاوﻧﺪ ﺗﻘﺪﻳﻢ ﺧﻮاهﻨﺪ ﻧﻤﻮد.',NULL),
('tafsiri_ot','',13,22 ,'ﻧﻈﺮت دور ﻣﺪار؛ اﻳﺸﺎن را ﺑﻪ هﻨﮕﺎم ﺧﺸﻢ و ﻏﻀﺐ ﺧﻮد، داوری ﻓﺮﻣﺎ و در ﺣﻀﻮر ﺧﻮد هﻼک ﻧﻤﺎ«! ﺁﻮزه ﺷﻜﺴﺘﻪ روزی ﺧﺪاوﻧﺪ ﻓﺮﻣﻮد آﻪ آﻮزهای ﺑﺨﺮم و ﺑﻪ آﺮد!',NULL),
('tafsiri_ot','',22,11 ,'و ﺑﻪ اﻳﺸﺎن ﺑﮕﻮﻳﻢ آﻪ ﭘﻴﺎم ﺧﺪاوﻧﺪ ﻗﺎدر ﻣﺘﻌﺎل اﻳﻦ اﺳﺖ: » هﻤﺎﻧﮕﻮﻧﻪ آﻪ اﻳﻦ آﻮزه ﺧﺮد ﺷﺪه و دﻳﮕﺮ ﻗﺎﺑﻞ ﺗﻌﻤﻴﺮ ،ﻧﻴﺴﺖ ﺑﺪﻳﻨﮕﻮﻧﻪ اورﺷﻠﻴﻢ و اهﺎﻟﯽ آن هﻢ از ﺑﻴﻦ ﺧﻮاهﻨﺪ رﻓﺖ. ﺗﻌﺪاد آﺸﺘﻪﺷﺪﮔﺎن ،ﻣﯽآورﻧﺪ ﻓﺮﻳﺎد و ﺷﻴﻮن از ﺧﺎﻧﻪهﺎﻳﺸﺎن ﺑﺮﺧﻴﺰد! زﻳﺮا ﺑﺮ ﺳﺮ راهﻢ دام ﮔﺴﺘﺮدهاﻧﺪ و ﺑﺮاﻳﻢ ﭼﺎه آﻨﺪهاﻧﺪ. 23ﺧﺪاوﻧﺪا، ﺗﻮ از ﺗﻤﺎم ﺗﻮﻃﺌﻪهﺎی اﻳﺸﺎن ﺑﺮای آﺸﺘﻦ ﻣﻦ آﮔﺎهﯽ؛ ﭘﺲ آﻧﻬﺎ را ﻧﺒﺨﺶ و ﮔﻨﺎهﺸﺎن را از ارﻣﻴﺎ',NULL),
('tafsiri_ot','',22,23 ,'ﮔﻠﻪ ء ارﻣﻴﺎ از ﺧﺪاوﻧﺪ ﺑﻘﺪری زﻳﺎد ﺧﻮاهﺪ ﺑﻮد آﻪ ﺟﻨﺎزههﺎ را در ﺗﻮﻓﺖ دﻓﻦ',NULL),
('tafsiri_ot','',6,24 ,'ﺗﻐﻴﻴﺮ ﻧﺨﻮاهﻢ داد. ﭘﺎدﺷﺎه ﺑﺎﺑﻞ اﻳﻦ ﺷﻬﺮ را ﺗﺴﺨﻴﺮ آﺮده، ﺑﺎ آﺗﺶ آن را از ﺑﻴﻦ ﺧﻮاهﺪ ﺑﺮد«. ﻣﻜﺎﻓﺎت ﺧﺎﻧﺪان ﺳﻠﻄﻨﺘﯽ 11و',NULL),
('tafsiri_ot','',6,12 ,'ﺧﺪاوﻧﺪ ﺑﻪ ﺧﺎﻧﺪان ﭘﺎدﺷﺎه ﻳﻬﻮدا آﻪ از ﻧﺴﻞ داود هﺴﺘﻨﺪ، ﭼﻨﻴﻦ ﻣﯽﻓﺮﻣﺎﻳﺪ: »ﺑﻪ هﻨﮕﺎم داوری، هﻤﻮاره ﺑﺎ ﻋﺪل و اﻧﺼﺎف ﻗﻀﺎوت آﻨﻴﺪ؛ از ﻣﻈﻠﻮم در ﻣﻘﺎﺑﻞ وﺣﺸﺖ ،ﺑﺎﺷﺪ',NULL),
('tafsiri_ot','',13,4 ,'آﻧﮕﺎه ﻣﻦ ﻓﺮﺳﺘﺎدﮔﺎن ﭘﺎدﺷﺎه را ﻧﺰد او ﺑﺎزﮔﺮداﻧﺪم ﺗﺎ ﺑﻪ وی ﺑﮕﻮﻳﻨﺪ آﻪ ،ﺧﺪاوﻧﺪ ﺧﺪای اﺳﺮاﺋﻴﻞ ﭼﻨﻴﻦ ﻣﯽﻓﺮﻣﺎﻳﺪ: »ﻣﻦ ﺳﻼﺣﻬﺎی ﺷﻤﺎ را آﻪ در ﺟﻨﮓ ﻋﻠﻴﻪ ﭘﺎدﺷﺎه ﺑﺎﺑﻞ و ﺳﭙﺎهﺶ ﺑﻜﺎر ﻣﯽﺑﺮﻳﺪ ﺑﯽاﺛﺮ ﺧﻮاهﻢ',NULL),
